import React, { useState } from 'react';
import './App.css';

function App() {
  // State hooks for each input
  const [dragline, setDragline] = useState('');
  const [method, setMethod] = useState('');
  const [dip, setDip] = useState('');
  const [width, setWidth] = useState('');
  const [coalThickness, setCoalThickness] = useState('');
  const [pass1Thickness, setPass1Thickness] = useState('');
  const [pass2Thickness, setPass2Thickness] = useState('');
  const [pass3Thickness, setPass3Thickness] = useState('');

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    const inputData = { dragline, method, dip, width, coalThickness, pass1Thickness, pass2Thickness, pass3Thickness };
    console.log(inputData);
    // Here you would send inputData to your ML model API
  };

  return (
    <div className="App">
      <h1>Inputs</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <div className="input-row">
          <div className="form-group">
            <label>Dragline</label>
            <input type="text" value={dragline} onChange={(e) => setDragline(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Method</label>
            <input type="text" value={method} onChange={(e) => setMethod(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Dip</label>
            <input type="number" value={dip} onChange={(e) => setDip(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Width</label>
            <input type="number" value={width} onChange={(e) => setWidth(e.target.value)} />
          </div>
        </div>
        <div className="input-row">
          <div className="form-group">
            <label>Coal Thickness</label>
            <input type="number" value={coalThickness} onChange={(e) => setCoalThickness(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Pass 1 Thickness</label>
            <input type="number" value={pass1Thickness} onChange={(e) => setPass1Thickness(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Pass 2 Thickness</label>
            <input type="number" value={pass2Thickness} onChange={(e) => setPass2Thickness(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Pass 3 Thickness</label>
            <input type="number" value={pass3Thickness} onChange={(e) => setPass3Thickness(e.target.value)} />
          </div>
        </div>
        <button type="submit">Run Model</button>
      </form>
    </div>
  );
}

export default App;
