import React, { useState } from 'react';
import './App.css';

function App() {
  // State hooks for each input
  const [dragline, setDragline] = useState('');
  const [method, setMethod] = useState('');
  const [dip, setDip] = useState('');
  const [width, setWidth] = useState('');
  const [coalthickness, setCoalThickness] = useState('');
  const [pass1thickness, setPass1Thickness] = useState('');
  const [pass2thickness, setPass2Thickness] = useState('');
  const [pass3thickness, setPass3Thickness] = useState('');

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    const inputData = { dragline, method, dip, width, coalthickness, pass1thickness, pass2thickness, pass3thickness};
    console.log(inputData);
    // Here you would send inputData to your ML model API
  };

    return (
      <div className="App">
        <h1>Inputs</h1>
        <form onSubmit={handleSubmit} className="form-container">
          <div className="form-row">
            <input type="text" placeholder="Dragline" value={dragline} onChange={(e) => setDragline(e.target.value)} />
            <input type="text" placeholder="Method" value={method} onChange={(e) => setMethod(e.target.value)} />
            <input type="number" placeholder="Dip" value={dip} onChange={(e) => setDip(e.target.value)} />
            <input type="number" placeholder="Width" value={width} onChange={(e) => setWidth(e.target.value)} />
          </div>
          <div className="form-row">
            <input type="number" placeholder="Coal Thickness" value={coalthickness} onChange={(e) => setCoalThickness(e.target.value)} />
            <input type="number" placeholder="Pass 1 Thickness" value={pass1thickness} onChange={(e) => setPass1Thickness(e.target.value)} />
            <input type="number" placeholder="Pass 2 Thickness" value={pass2thickness} onChange={(e) => setPass2Thickness(e.target.value)} />
            <input type="number" placeholder="Pass 3 Thickness" value={pass3thickness} onChange={(e) => setPass3Thickness(e.target.value)} />
          </div>
          <button type="submit">Run Model</button>
        </form>
      </div>
    );
  }

  export default App;
